// Minified main.js
const menuButton = document.getElementById("menuButton"),
  overlayMenu = document.getElementById("mainNav"),
  menuX = menuButton.querySelector(".menu-x"),
  menuBars = menuButton.querySelectorAll(".menu-icon-bar");
menuButton.addEventListener("click", function () {
  overlayMenu.classList.toggle("open");
  const e = "true" === menuButton.getAttribute("aria-expanded");
  menuButton.setAttribute("aria-expanded", !e),
    overlayMenu.classList.contains("open")
      ? ((menuX.style.display = "flex"),
        menuBars.forEach((e) => (e.style.display = "none")))
      : ((menuX.style.display = "none"),
        menuBars.forEach((e) => (e.style.display = "block")));
}),
  window.addEventListener("keydown", function (e) {
    "Escape" === e.key &&
      overlayMenu.classList.contains("open") &&
      (overlayMenu.classList.remove("open"),
      menuButton.setAttribute("aria-expanded", !1),
      (menuX.style.display = "none"),
      menuBars.forEach((e) => (e.style.display = "block")));
  });
const form = document.querySelector(".contact-form");
form &&
  form.addEventListener("submit", function (e) {
    form.querySelector('input[name="category"]:checked') ||
      (e.preventDefault(), alert("You must select Sips, Grub or Other!"));
  });
